import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';

function TodoApp() {
  const [tasks, setTasks] = useState(() => {
    // Retrieve tasks from local storage at initial render
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });
  const [task, setTask] = useState('');

  useEffect(() => {
    // Save tasks to local storage whenever tasks state changes
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);
  useEffect(() => {
    // Add jQuery click event listener
    $('.list-group-item').click(function() {
      $(this).fadeOut();
    });
  }, [tasks]);

  const addTask = () => {
    if (task) {
      setTasks([...tasks, task]);
      setTask('');
    }
  };

  const deleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  const updateTask = (index) => {
    const newTask = prompt('Update the task:', tasks[index]);
    if (newTask) {
      const newTasks = tasks.map((item, i) => (i === index ? newTask : item));
      setTasks(newTasks);
    }
  };

  return (
    <div style={{ backgroundColor: '#ce35fd', minHeight: '100vh'}}>
      <h2 className="text-center mb-4">Get Things Done!</h2>
    <div className="header-custom d-flex justify-content-between align-items-center">
  <input
    type="text"
    className="form-control input-custom me-2" // Added 'me-2' for margin-end (right margin)
    placeholder="What is the task today?"
    id="taskInput"
    onChange={(e) => setTask(e.target.value)}
  />
  <button className="btn btn-primary btn-sm" onClick={addTask}>
    Add Task
  </button>
</div>

      <ul className="list-group">
        {tasks.map((item, index) => (
          <li key={index} className="list-group-item list-item-custom d-flex justify-content-between align-items-center">
            <input type="checkbox" />
            {item}
            <button className="btn button-custom" onClick={() => updateTask(index)}>
              ‚úèÔ∏è
            </button>
            <button className="btn button-custom" onClick={() => deleteTask(index)}>
              üóëÔ∏è
            </button>
          </li>
        ))}
      </ul>
      </div>

  );
}

export default TodoApp;
