import React, { useState, useEffect } from 'react';
import $ from 'jquery';
import 'bootstrap/dist/css/bootstrap.min.css';

function TodoApp() {
  const [tasks, setTasks] = useState(() => {
    // Retrieve tasks from local storage at initial render
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });
  const [task, setTask] = useState('');

  useEffect(() => {
    // Save tasks to local storage whenever tasks state changes
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (task) {
      setTasks([...tasks, task]);
      setTask('');
      $('#taskInput').val('');
    }
  };

  const deleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  const updateTask = (index) => {
    const newTask = prompt('Update the task:', tasks[index]);
    if (newTask) {
      const newTasks = tasks.map((item, i) => (i === index ? newTask : item));
      setTasks(newTasks);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">To-Do List App</h2>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Add a new task"
          id="taskInput"
          onChange={(e) => setTask(e.target.value)}
        />
        <div className="input-group-append">
          <button className="btn btn-outline-primary" onClick={addTask}>
            Add Task
          </button>
        </div>
      </div>
      <ul className="list-group">
        {tasks.map((item, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
            {item}
            <div>
              <button className="btn btn-outline-success btn-sm mr-2" onClick={() => updateTask(index)}>
                Update
              </button>
              <button className="btn btn-outline-danger btn-sm" onClick={() => deleteTask(index)}>
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoApp;
