import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function TodoApp() {
  const [tasks, setTasks] = useState(() => {
    // Retrieve tasks from local storage at initial render
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });
  const [task, setTask] = useState('');

  useEffect(() => {
    // Save tasks to local storage whenever tasks state changes
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (task) {
      setTasks([...tasks, task]);
      setTask('');
    }
  };

  const deleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  const updateTask = (index) => {
    const newTask = prompt('Update the task:', tasks[index]);
    if (newTask) {
      const newTasks = tasks.map((item, i) => (i === index ? newTask : item));
      setTasks(newTasks);
    }
  };

  return (

    //<div className="container bg-dark text-white my-5 p-4 rounded">
    <div style={{ backgroundColor: '#6f42c1', minHeight: '100vh' }}>
      <div className="container bg-primary text-white my-5 p-4 rounded">
      <h2 className="text-center mb-4">My To-Do List</h2>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Add a new task"
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <button className="btn btn-primary" onClick={addTask}>
          Add
        </button>
      </div>
      <ul className="list-group">
        {tasks.map((item, index) => (
          <li key={index} className="list-group-item bg-secondary text-white d-flex justify-content-between align-items-center">
            <span>{item}</span>
            <div>
              <button className="btn btn-info me-2" onClick={() => updateTask(index)}>
                Edit
              </button>
              <button className="btn btn-danger" onClick={() => deleteTask(index)}>
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
      </div>
   </div>
  );
}

export default TodoApp;
